import { Text } from '@react-three/drei'
import { Canvas } from '@react-three/fiber'
import { ARButton, Controllers, Interactive, XR } from '@react-three/xr'
import React, { FC, Suspense, useState } from 'react'

interface BoxProps {
  color: string | number
  size: [number, number, number]
  scale: [number, number, number]
  position?: [number, number, number]
}

const Box: FC<BoxProps> = ({ color, size, scale, children, ...rest }) => {
  return (
    <mesh scale={scale} {...rest}>
      <boxBufferGeometry args={size} />
      <meshPhongMaterial color={color} />
      {children}
    </mesh>
  )
}

const Button: FC<any> = (props) => {
  const [hover, setHover] = useState(false)
  const [color, setColor] = useState<string | number>('blue')

  const onSelect = () => {
    setColor((Math.random() * 0xffffff) | 0)
  }

  return (
    <Interactive onHover={() => setHover(true)} onBlur={() => setHover(false)} onSelect={onSelect}>
      <Box color={color} scale={hover ? [0.6, 0.6, 0.6] : [0.5, 0.5, 0.5]} size={[0.4, 0.1, 0.1]} {...props}>
        <Suspense fallback={null}>
          <Text position={[0, 0, 0.06]} fontSize={0.05} color="#000" anchorX="center" anchorY="middle">
            Ge Gao's first AR
          </Text>
        </Suspense>
      </Box>
    </Interactive>
  )
}

export const App: FC = () => {
  return (
    <>
      <ARButton />
      <Canvas>
        <XR referenceSpace="local">
          <ambientLight />
          <pointLight position={[10, 10, 10]} />
          <Button position={[0, 0.1, -0.2]} />
          <Controllers />
        </XR>
      </Canvas>
    </>
  )
}
